from textblob import TextBlobdef analyze_sentiment(text):    # Create a TextBlob object    blob = TextBlob(text)    # Get the sentiment polarity (ranges from -1 to 1)    polarity = blob.sentiment.polarity    # Classify the sentiment as positive, negative, or neutral    if polarity > 0:        sentiment = 'positive'    elif polarity < 0:        sentiment = 'negative'    else:        sentiment = 'neutral'    return sentimentdef respond_to_sentiment(sentiment):    if sentiment == 'positive':        return "That's great! I'm glad to hear that."    elif sentiment == 'negative':        return "I'm sorry to hear that. Is there anything I can do to help?"    elif sentiment == 'neutral':        return "Thanks for sharing. Anything else on your mind?"